Scripts.OnClick:
if ( self.sortType ) then
    MasterLootLogger:SortLoot(self.sortType);
end
PlaySound("igMainMenuOptionCheckBoxOn");

Scripts.OnLeave:
                        if (not (MasterLootLogger.selectedItemIndex and self:GetParent():GetID() + FauxScrollFrame_GetOffset(MasterLootLogger.scrollframe) == MasterLootLogger.selectedItemIndex)) then
						    self:GetParent():UnlockHighlight();
                        end
						GameTooltip:Hide();
